import React, { useEffect } from "react";

const DynamicText = () => {
  useEffect(() => {
    const target = document.getElementById("text-target");
    let array = ["simple", "clear", "smart", "funny"];
    let wordInd = 0;
    let letterIndex = 0;

    const createLetter = () => {
      const letter = document.createElement("span");
      target.appendChild(letter);
      // Cette fonction creer un enfant a text-target
      // Cette enfant est un span

      letter.classList.add("letter");
      letter.style.opacity = "0";
      letter.style.animation = "anim 5s ease forwards";
      letter.textContent = array[wordInd][letterIndex];

      setTimeout(() => {
        letter.remove();
      }, 2000);

      const loop = () => {
        setTimeout(() => {
          createLetter();
          letterIndex++;
          loop();
        }, 80);
      };

      loop();
    };
    createLetter();
  }, []);

  return (
    <span className="dynamic-text">
      <span>simply</span>
      <span id="text-target"></span>
    </span>
  );
};

export default DynamicText;
